public class Magician extends Role{
    // 治癒力
    private int healPower;



    // 建構子：初始化魔法師的名稱、生命值和攻擊力
    public Magician(String name, int health, int attackPower, int healPower) {
        super(name, health, attackPower);
        this.healPower = healPower;
    }

    // 取得治癒力
    public int getHealPower() {
        return healPower;
    }

    // 攻擊對手(劍客/魔法師)，父類別的參考指到子類別物件
    @Override
    public void attack(Role opponent) {
        opponent.setHealth(opponent.getHealth() - this.getAttackPower());
        System.out.println(this.getName() + " 魔法攻擊 " + opponent.getName() + " 造成 " +
                this.getAttackPower() + " 點傷害。" + opponent);
    }

    // 治療隊友(劍客/魔法師)，父類別的參考指到子類別物件
    public void heal(Role ally) {
        ally.setHealth(ally.getHealth() + this.healPower);
        System.out.println(this.getName() + " 治療 " + ally.getName() + " 回復 " + healPower + " 點生命值。" + ally);
    }

    @Override
    public String toString() {
        return super.toString() + ", 治癒力: " + healPower;
    }


    @Override
    public void showSpecialSkill() {
        System.out.println("╔═════════════════════════════╗");
        System.out.println("║ " + name + "的特殊技能        ║");
        System.out.println("╠═════════════════════════════╣");
        System.out.println("║ 技能名稱：元素爆發          ║");
        System.out.println("║ 技能描述：召喚強大魔法攻擊  ║");
        System.out.println("║ 技能效果：範圍魔法傷害      ║");
        System.out.println("║ 額外效果：恢復自身魔力      ║");
        System.out.println("╚═════════════════════════════╝");
    }

    // 具體方法定義「演算法骨架」
    public void takeDamage(int damage) {
        // 步驟 1: 扣血（所有角色相同）
        this.health -= damage;

        // 步驟 2: 顯示訊息（所有角色相同）
        System.out.println("受到傷害");

        // 步驟 3: 檢查死亡（所有角色相同）
        if (!isAlive()) {
            // 步驟 4: 死亡處理（每個角色不同 - 抽象方法）
            onDeath();
        }
    }

}

public class Magician extends Role{
    // 治癒力
    private int healPower;



    // 建構子：初始化魔法師的名稱、生命值和攻擊力
    public Magician(String name, int health, int attackPower, int healPower) {
        super(name, health, attackPower);
        this.healPower = healPower;
    }

    // 取得治癒力
    public int getHealPower() {
        return healPower;
    }

    // 攻擊對手(劍客/魔法師)，使用 Role.takeDamage
    @Override
    public void attack(Role opponent) {
        System.out.println("✨ " + this.getName() + " 施放魔法攻擊 " + opponent.getName() + "！");
        opponent.takeDamage(this.getAttackPower());
    }

    // 治療隊友(劍客/魔法師)
    public void heal(Role ally) {
        ally.setHealth(ally.getHealth() + this.healPower);
        System.out.println(this.getName() + " 治療 " + ally.getName() + " 回復 " + healPower + " 點生命值。" + ally);
    }

    @Override
    public String toString() {
        return super.toString() + ", 治癒力: " + healPower;
    }


    @Override
    public void showSpecialSkill() {
        System.out.println("╔═════════════════════════════╗");
        System.out.println("║ " + name + "的特殊技能        ║");
        System.out.println("╠═════════════════════════════╣");
        System.out.println("║ 技能名稱：元素爆發          ║");
        System.out.println("║ 技能描述：召喚強大魔法攻擊  ║");
        System.out.println("║ 技能效果：範圍魔法傷害      ║");
        System.out.println("║ 額外效果：恢復自身魔力      ║");
        System.out.println("╚═════════════════════════════╝");
    }

    // 戰鬥前的準備動作
    @Override
    public void prepareBattle() {
        System.out.println(" " + name + " 翻開魔法書，開始吟唱古老的咒語...");
        System.out.println("✨ 魔法能量在周圍凝聚，空氣中閃爍著神秘的光芒。");
    }

    // 戰鬥後的行為
    @Override
    public void afterBattle() {
        System.out.println(" " + name + " 閉目冥想，恢復消耗的魔力。\n");
    }

    // 死亡時的處理
    @Override
    public void onDeath() {
        System.out.println(" " + name + " 的生命之火熄滅了...");
        System.out.println("✨ " + name + " 的身體化為無數魔法粒子，消散在空氣中。");
        System.out.println(" 魔法書掉落在地上，微微發光。\n");
    }

}

public abstract class Role {
    // 角色名稱
    public String name;
    // 生命值
    public int health;
    // 攻擊力
    private int attackPower;

    // 建構子：初始化角色的名稱、生命值和攻擊力
    public Role(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }


    // 取得角色名稱
    public String getName() {
        return name;
    }

    // 取得生命值
    public int getHealth() {
        return health;
    }

    // 取得攻擊力
    public int getAttackPower() {
        return attackPower;
    }
    // 設定生命值
    public void setHealth(int health) {
        this.health = health;
    }

    // 檢查角色是否存活
    public boolean isAlive() {
        return health > 0;
    }

    public abstract void attack(Role opponent);

    /**
     * 展示角色的特殊技能
     * 為什麼設計成抽象方法？
     * 1. 每個角色都有特殊技能（共同規格）
     * 2. 但每個角色的技能內容都不同（個別實作）
     * 3. 我們無法在 Role 類別中提供一個「適合所有角色」的預設實作
     */
    public abstract void showSpecialSkill();


    /**
     * 死亡時的處理（抽象方法）
     * 每個角色死亡時的表現方式都不同
     */
    public abstract void onDeath();

    // 戰鬥前的準備動作
    public abstract void prepareBattle();

    // 戰鬥後的行為
    public abstract void afterBattle();


    /**
     * 受到傷害（具體方法）
     * 所有角色受傷的基本流程都相同：
     * 1. 扣除生命值
     * 2. 顯示受傷訊息
     * 3. 檢查是否死亡
     * 4. 如果死亡，呼叫 onDeath()
     */
    public void takeDamage(int damage) {
        this.health -= damage;
        System.out.println(" " + name + " 受到 " + damage + " 點傷害！目前生命值：" + health);

        if (!isAlive()) {
            System.out.println("\n[某角色生命值降為 0]\n");
            onDeath(); // 呼叫抽象方法
        }
    }




    @Override
    public String toString() {
        return "角色名稱: " + name + ", 生命值: " + health;
    }
}

public abstract class Role {
    // 角色名稱
    public String name;
    // 生命值
    public int health;
    // 攻擊力
    private int attackPower;

    // 建構子：初始化角色的名稱、生命值和攻擊力
    public Role(String name, int health, int attackPower) {
        this.name = name;
        this.health = health;
        this.attackPower = attackPower;
    }


    // 取得角色名稱
    public String getName() {
        return name;
    }

    // 取得生命值
    public int getHealth() {
        return health;
    }

    // 取得攻擊力
    public int getAttackPower() {
        return attackPower;
    }
    // 設定生命值
    public void setHealth(int health) {
        this.health = health;
    }

    // 檢查角色是否存活
    public boolean isAlive() {
        return health > 0;
    }

    public abstract void attack(Role opponent);

    /**
     * 展示角色的特殊技能
     * 為什麼設計成抽象方法？
     * 1. 每個角色都有特殊技能（共同規格）
     * 2. 但每個角色的技能內容都不同（個別實作）
     * 3. 我們無法在 Role 類別中提供一個「適合所有角色」的預設實作
     */
    public abstract void showSpecialSkill();


    /**
     * 死亡時的處理（抽象方法）
     * 每個角色死亡時的表現方式都不同
     */
    public abstract void onDeath();

    // 戰鬥前的準備動作
    public abstract void prepareBattle();

    // 戰鬥後的行為
    public abstract void afterBattle();


    /**
     * 受到傷害（具體方法）
     * 所有角色受傷的基本流程都相同：
     * 1. 扣除生命值
     * 2. 顯示受傷訊息
     * 3. 檢查是否死亡
     * 4. 如果死亡，呼叫 onDeath()
     */
    public void takeDamage(int damage) {
        this.health -= damage;
        System.out.println(" " + name + " 受到 " + damage + " 點傷害！目前生命值：" + health);

        if (!isAlive()) {
            onDeath(); // 呼叫抽象方法
        }
    }




    @Override
    public String toString() {
        return "角色名稱: " + name + ", 生命值: " + health;
    }
}

public class ShieldSwordsMan extends SwordsMan{
    private int defenseCapacity;
    // 建構子：初始化持盾劍士的名稱、生命值和攻擊力
    public ShieldSwordsMan(String name, int health, int attackPower, int defenseCapacity) {
        super(name, health, attackPower);
        this.defenseCapacity = defenseCapacity;
    }

    // 攻擊對手(劍客/魔法師)，父類別的參考指到子類別物件
    @Override
    public void attack(Role opponent) {
        int reducedDamage = this.getAttackPower() - 5; // 持盾劍士攻擊力減少5點
        opponent.setHealth(opponent.getHealth() - reducedDamage);
        System.out.println(this.getName() + " 揮劍攻擊 " + opponent.getName() + " 造成 " +
                reducedDamage + " 點傷害。" + opponent);
    }

    public int getDefenseCapacity() {
        return defenseCapacity;
    }

    public void defence() {
        this.setHealth(this.getHealth() + defenseCapacity);
        System.out.println(this.getName() + " 使用盾牌防禦，恢復 " + defenseCapacity + " 點生命值。" + this);
    }

    @Override
    public void showSpecialSkill() {
        System.out.println("╔═════════════════════════════╗");
        System.out.println("║ " + name + "的特殊技能        ║");
        System.out.println("╠═════════════════════════════╣");
        System.out.println("║ 技能名稱：盾牌猛擊          ║");
        System.out.println("║ 技能描述：使用盾牌撞擊敵人  ║");
        System.out.println("║ 技能效果：造成傷害並暈眩       ║");
        System.out.println("║ 防禦加成：+8 防禦力      ║");
        System.out.println("╚═════════════════════════════╝");
    }

    // 具體方法定義「演算法骨架」
    public void takeDamage(int damage) {
        // 步驟 1: 扣血（所有角色相同）
        this.health -= damage;

        // 步驟 2: 顯示訊息（所有角色相同）
        System.out.println("受到傷害");

        // 步驟 3: 檢查死亡（所有角色相同）
        if (!isAlive()) {
            // 步驟 4: 死亡處理（每個角色不同 - 抽象方法）
            onDeath();
        }
    }

}

public class ShieldSwordsMan extends SwordsMan{
    private int defenseCapacity;
    // 建構子：初始化持盾劍士的名稱、生命值和攻擊力
    public ShieldSwordsMan(String name, int health, int attackPower, int defenseCapacity) {
        super(name, health, attackPower);
        this.defenseCapacity = defenseCapacity;
    }

    // 攻擊對手(劍客/魔法師)，父類別的參考指到子類別物件
    @Override
    public void attack(Role opponent) {
        int reducedDamage = this.getAttackPower() - 5; // 持盾劍士攻擊力減少5點
        System.out.println("⚔️  " + this.getName() + " 揮劍攻擊 " + opponent.getName() + "！");
        opponent.takeDamage(reducedDamage);
    }

    public int getDefenseCapacity() {
        return defenseCapacity;
    }

    public void defence() {
        this.setHealth(this.getHealth() + defenseCapacity);
        System.out.println(this.getName() + " 使用盾牌防禦，恢復 " + defenseCapacity + " 點生命值。" + this);
    }

    @Override
    public void showSpecialSkill() {
        System.out.println("╔═════════════════════════════╗");
        System.out.println("║ " + name + "的特殊技能        ║");
        System.out.println("╠═════════════════════════════╣");
        System.out.println("║ 技能名稱：盾牌猛擊          ║");
        System.out.println("║ 技能描述：使用盾牌撞擊敵人  ║");
        System.out.println("║ 技能效果：造成傷害並暈眩       ║");
        System.out.println("║ 防禦加成：+8 防禦力      ║");
        System.out.println("╚═════════════════════════════╝");
    }

    // 戰鬥前的準備動作
    @Override
    public void prepareBattle() {
        System.out.println("️  " + name + " 高舉盾牌，警戒四周，準備防守...");
    }

    // 戰鬥後的行為
    @Override
    public void afterBattle() {
        System.out.println(name + " 放下盾牌，整理裝備。\n");
    }

    // 死亡時的處理
    @Override
    public void onDeath() {
        System.out.println(" " + name + " 被擊倒，盾牌碎裂在地...");
        System.out.println(name + " 慢慢倒下，戰鬥結束。\n");
    }

}

